<div 
    x-data="{isShow: false}" 
    x-show="isShow" 
    x-transition 
    x-init="() => {
        window.addEventListener('scroll', () => {
            if (window.scrollY > 300) {
                $data.isShow = true;
            } else {
                $data.isShow = false;
            }
        })
    }"    
    class="fixed bottom-[80px] right-8 w-10 h-10 text-white bg-primary-500 hover:bg-primary-600 transition-all rounded-full z-50"
>
    <span 
        class="w-full h-full flex items-center justify-center cursor-pointer"
        @click="window.scrollTo({top: 0, behavior: 'smooth'})"
    >
        <x-carbon-chevron-up class="size-6" />
    </span>
</div>